<script>
  function updateTitle(k){
      if($('input[name="bedrooms"]').val()=='' && $('input[name="bathrooms"]').val()==''){
          $('#port-mre-btn').html('<?php echo $bed_html;?>')
      }else{
          var htmlbd = ''; 
          if($('input[name="bedrooms"]').val()!=''){
              htmlbd += $('input[name="bedrooms"]').val()+' Beds';
          }
          if($('input[name="bedrooms"]').val()!='' && $('input[name="bathrooms"]').val()!=''){
               htmlbd += ' & ';
          }
           if($('input[name="bathrooms"]').val()!=''){
              htmlbd += $('input[name="bathrooms"]').val()+' Baths';
          }
           $('#port-mre-btn').html(htmlbd)
      }
  }
	function showbedbath(k){
	     $('#port-mre-btn').html('<?php echo $bed_html;?>')
	    if($(k).attr('data-val')=='residential'){
	          $('.bed-bath').find('.btnSelected').removeClass('.btnSelected');
	        $('.bed-bath').removeClass('d-none'); 
	        $('input[name="bedrooms"]').val('');
	         $('input[name="bathrooms"]').val('');
	    }else{
	        $('.bed-bath').find('.btnSelected').removeClass('.btnSelected');
	         $('.bed-bath').addClass('d-none');
	           $('input[name="bedrooms"]').val('');
	         $('input[name="bathrooms"]').val('');
	    }
	}
    $(function(){
        
        $(document).click(function (e) {
    e.stopPropagation();
    var $dropdowns = $('.form-container-list-item')
    
    $dropdowns.not($dropdowns.has(event.target)).removeClass('active');
     
})
        
    })
</script>
 
